    private void Tabla_datosMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_Tabla_datosMouseClicked
        // TODO add your handling code here:
        int row = Tabla_datos.getSelectedRow();
        this.txtCodUsu.setText(Tabla_datos.getModel().getValueAt(row, 0).toString());
        this.txtCEmp.setText(Tabla_datos.getModel().getValueAt(row, 1).toString());
        this.txtUid.setText(Tabla_datos.getModel().getValueAt(row, 2).toString());

      
    }//GEN-LAST:event_Tabla_datosMouseClicked

